package Assignments;

public class Assignment3_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte data1=10;
		System.out.println("Byte value:"+ data1);
		
		short data2=100;
		System.out.println("Short value:"+ data2);
		
		int data3=5000;
		System.out.println("Int value:"+ data3);
		
		long data4=9000000000000L;
		System.out.println("Long value:"+ data4);
		
		float data5=5.4546546F;
		System.out.println("Float value:"+ data5);
		
		double data6=99.99999999;
		System.out.println("Double value:"+ data6);
		
		boolean data7=true;
		System.out.println("Boolean value:"+ data7);
		
		char data8='A';
		System.out.println("Char value:"+ data8);
		
	
	}

}
