package day3;

public class Program7_ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int num=1;num<=10;num++)
		{
			System.out.print(num+" ");
		}
		
		System.out.println();
		
		for(int num=10;num>=1;num--)
		{
			System.out.print(num+" ");
		}
	}

}
